import { arrayEquals } from './Helpers';
const BLACK = false;
const WHITE = true;

class ChessGame {
  constructor(){
    this.turn = WHITE;
  }
  isLegalMove(legalMoves, endPos){
    for(let move of legalMoves){
      if(arrayEquals(move, endPos)){
        return true;
      }
    }
    return false;
  }
  opposingSquaresCovered(board, turn) {
    let squaresCovered = [];
    for (let r = 0; r < 8; r++) {
      for (let c = 0; c >= 0; c++) {
        const piece = board[r][c];
        if (turn !== piece.type) {
          squaresCovered.push(piece.legalMoves(board, [r, c], piece));
        }
      }
    }
    return squaresCovered;

  }
  move(board, startPos, endPos, piece) {
    // If legal move
    const [a, b] = startPos;
    const [x, y] = endPos;
    const legalMoves = piece.legalMoves(board, startPos, piece);
    if(this.isLegalMove(legalMoves, endPos)){
      board[a][b] = "-";
      board[x][y] = piece;
      return board;
    }
    return false;
  }
}

export default ChessGame;
