import { arrayEquals } from './Helpers';
import { King } from './Pieces';

const BLACK = false;
const WHITE = true;

class ChessGame {
  constructor(){
    this.turn = WHITE;
  }
  isLegalMove(legalMoves, endPos){
    // Check that move is legal
    for(let move of legalMoves){
      if(arrayEquals(move, endPos)){
        return true;
      }
    }
  }
  move(board, startPos, endPos, piece, kingPos) {
    // If legal move
    const [a, b] = startPos;
    const [x, y] = endPos;
    const legalMoves = piece.allowedMoves(board, startPos, piece, kingPos);
    if(this.isLegalMove(legalMoves, endPos)){
      if (piece instanceof King) {
        kingPos = endPos;
      }
      board[a][b] = "-";
      board[x][y] = piece;
      return [board, kingPos];
    }
    return false;
  }
}

export default ChessGame;
