import './Board.css'
import React from 'react'

class Board extends React.Component {
  static defaultProps = {
    nRows: 8,
    nCols: 8
  }
  render(){
    let board = []
    for(let i = 0; i < this.props.nRows; i++){
      let row = []
      for(let j = 0; j < this.props.nCols; j++){
        row.push(<td className='square'></td>)
      }
      board.push(<tr>{row}</tr>)   
    }
    return (
      <table>
        <tbody>
          {board}
        </tbody>
      </table>
    );
  }
}

export default Board;
