import './Board.css'
import React from 'react'

class Board extends React.Component {
  static defaultProps = {
    nRows: 8,
    nCols: 8,
  }
  render(){
    let board = [];
    let cOdd = true;
    let rOdd = true;
    for(let i = this.props.nRows; i > 0; i--){
      let row = [] 
      cOdd = rOdd
      for(let j = 0; j < this.props.nCols; j++){
        let ltr = `${String.fromCharCode(97 + j)}${i}`
        cOdd = !cOdd;
        clr = cOdd? 'odd': 'even'

        row.push(<td key={ltr} className='square'></td>)
      }
      board.push(<tr>{row}</tr>)   
      rOdd = !rOdd;
    }
    return (
      <div>
        <h1>ChessBoard</h1>
        <table>
          <tbody>
            {board}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Board;
