import './Board.css'
import React from 'react'
import Square from './Square';

class Board extends React.Component {
  static defaultProps = {
    nRows: 8,
    nCols: 8,
  }
  constructor(props) {
    super(props);
    this.state = {
      board: this.setupBoard(),
      selectedPiece: [-1, -1]
    }
  }
  arrayEquals(a, b) {
    return Array.isArray(a) &&
        Array.isArray(b) &&
        a.length === b.length &&
        a.every((val, index) => val === b[index]);
  }

  selectPiece(pos) {
    if(this.arrayEquals(pos, this.state.selectedPiece)){
      this.setState({selectedPiece: [-1, -1]})
    }
    this.setState({selectedPiece: pos})
  }

  setupBoard() {
    let board = [];
    const {nRows, nCols} = this.props;
    // Generate Empty Board
    for (let r = nRows - 1; r >= 0; r--) {
      let row = []
      for (let c = 0; c < nCols; c++) {
        row.push("-") 
      }
      board.push(row)
    }
    // Display Black Pieces
    board[0][0] = "b_r";
    board[0][1] = "b_kn";
    board[0][2] = "b_b";
    board[0][3] = "b_q";
    board[0][4] = "b_k";
    board[0][5] = "b_b";
    board[0][6] = "b_kn";
    board[0][7] = "b_r";
    // Display White Pieces
    board[7][0] = "w_r";
    board[7][1] = "w_kn";
    board[7][2] = "w_b";
    board[7][3] = "w_q";
    board[7][4] = "w_k";
    board[7][5] = "w_b";
    board[7][6] = "w_kn";
    board[7][7] = "w_r";
    // Display Pawns
    for(let c = 0; c < 8; c++){
      board[1][c] = "b_p";
      board[6][c] = "w_p";
    }
    return board;
  }
  render(){
    console.log("Rerender")
    // Render Chess Board
    let board = [];
    let cOdd = true;
    let rOdd = true;
    for(let i = this.props.nRows - 1; i >= 0; i--){
      let row = [] 
      cOdd = rOdd
      for(let j = 0; j < this.props.nCols; j++){
        const sqr = `${String.fromCharCode(97 + j)}${i}`
        const isDark = cOdd? true: false;
        const piece = this.state.board[i][j];
        const pos = [i, j];
        const isSelected = pos.every((val, idx) => val === this.state.selectedPiece[idx]);
        console.log("pos = "+pos+" state= "+this.state.selectedPiece)
        row.push(<Square key={sqr} isDark={isDark} piece={piece} selectPiece={() => this.selectPiece([i,j])} isSelected={isSelected}/>)
        cOdd = !cOdd;
      }
      board.push(<tr key={i}>{row}</tr>)   
      rOdd = !rOdd;

      // Render Chess Pieces
    }
    return (
      <div>
        <table>
          <tbody>
            {board}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Board;
