import './Square.css';
import React from 'react';

class Square extends React.Component {
  constructor(props){
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }
  handleClick() {
    console.log("Yeet");
    this.props.selectPiece();
  }
  render(){
    const classes = "square" + (this.props.isDark ? " dark" : " light") + (this.props.isSelected ? " selected": "");
    const {piece} = this.props;
    return (
      <td onClick={this.handleClick} className={classes}>
        {piece !== '-'? 
          <img class="piece" src={`./images/${piece.imgName}.png`}></img> //https://commons.wikimedia.org/wiki/Category:SVG_chess_pieces
        : ''}
      </td>
    );
  }
}

export default Square;
