import './Square.css';
import React from 'react';

class Square extends React.Component {
  constructor(props){
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }
  handleClick() {
    console.log("Yeet");
    this.props.selectPiece();
  }
  render(){
    const classes = "square" + (this.props.isDark ? " dark" : " light") + (this.props.isSelected ? " selected": "") + (this.props.isLegal ? " legal": "");
    const {piece} = this.props;
    return (
      <td onClick={this.handleClick} className={classes}>
        {piece !== '-'? 
        <div>
          <img className="piece" src={`./images/${piece.imgName}.png`}/> //https://commons.wikimedia.org/wiki/Category:SVG_chess_pieces
          <i class="fa-solid fa-circle"></i>
        </div>
        : ''}
      </td>
    );
  }
}

export default Square;
