const BLACK = 0;
const WHITE = 1;

class Game {
  constructor(){
    this.turn = WHITE;
    this.selectedPiece = false;
  }

  move(board, startPos, endPos, piece) {
    // If legal move
    let [a, b] = startPos;
    let [x, y] = endPos;

    board[a][b] = "-";
    board[x][y] = piece;

    return board;
  }
}

export default Game;
