import './Board.css'
import React from 'react'
import Square from './Square';
import {Pawn, Bishop, Knight, Rook, Queen, King} from './Pieces';
import { arrayEquals } from './Helpers';
import Game from './Game';

const BLACK = 0
const WHITE = 1

let game = new Game();

class Board extends React.Component {
  static defaultProps = {
    nRows: 8,
    nCols: 8,
  }
  constructor(props) {
    super(props);
    this.state = {
      board: this.setupBoard(),
      selectedPiece: false
    }
  }

  selectPiece(pos) {
    if(this.arrayEquals(pos, this.state.selectedPiece)){
      console.log(true)
      this.setState({selectedPiece: false})
      this.game.SelectedPiece = false;
    }
    this.setState({selectedPiece: pos});
    this.game.selectedPiece = pos;
  }

  setupBoard() {
    let board = [];
    const {nRows, nCols} = this.props;
    // Generate Empty Board
    for (let r = nRows - 1; r >= 0; r--) {
      let row = []
      for (let c = 0; c < nCols; c++) {
        row.push("-") 
      }
      board.push(row)
    }
    // Display White Pieces
    board[0][0] = new Rook(WHITE, "w_r");
    board[0][1] = new Knight(WHITE, "w_kn");
    board[0][2] = new Bishop(WHITE, "w_b");
    board[0][3] = new Queen(WHITE, "w_q");
    board[0][4] = new King(WHITE, "w_k");
    board[0][5] = new Bishop(WHITE, "w_b");
    board[0][6] = new Knight(WHITE, "w_kn");
    board[0][7] = new Rook(WHITE, "w_r");
    // Display Black Pieces
    board[7][0] = new Rook(BLACK, "b_r");
    board[7][1] = new Knight(BLACK, "b_kn");
    board[7][2] = new Bishop(BLACK, "b_b");
    board[7][3] = new Queen(BLACK, "b_q");
    board[7][4] = new King(BLACK, "b_k");
    board[7][5] = new Bishop(BLACK, "b_b");
    board[7][6] = new Knight(BLACK, "b_kn");
    board[7][7] = new Rook(BLACK, "b_r");
    // Display Pawns
    for(let c = 0; c < 8; c++){
      board[1][c] = new Pawn(WHITE, "w_p");
      board[6][c] = new Pawn(BLACK, "b_p");
    }
    return board;
  }
  render(){
    console.log("Rerender")
    // Render Chess Board
    let board = [];
    let cOdd = true;
    let rOdd = true;
    for(let i = this.props.nRows - 1; i >= 0; i--){
      let row = [] 
      cOdd = rOdd
      for(let j = 0; j < this.props.nCols; j++){
        const sqr = `${String.fromCharCode(97 + j)}${i}`
        const isDark = cOdd? true: false;
        const piece = this.state.board[i][j];
        const pos = [i, j];
        const isSelected = pos.every((val, idx) => val === this.state.selectedPiece[idx]);
        console.log("pos = "+pos+" state= "+this.state.selectedPiece)
        row.push(<Square key={sqr} isDark={isDark} piece={piece} selectPiece={() => this.selectPiece([i,j])} isSelected={isSelected}/>)
        cOdd = !cOdd;
      }
      board.push(<tr key={i}>{row}</tr>)   
      rOdd = !rOdd;

      // Render Chess Pieces
    }
    return (
      <div>
        <table>
          <tbody>
            {board}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Board;
