// Abstract Class
class Piece {
  constructor(type, img, pos){
    if(this.constructor === Piece){
      throw new Error('Class "Piece" cannot be instantiated');
    }
    this.type = type;
    this.imgName = img;
    this.pos = pos;
  }
  // True if row and column are on the board (8x8)
  onBoard(r, c){
    if(r > 7 || r < 0 || c > 7 || c < 0){
      return false;
    }
    return true;
  }
  // True if piece on the square is the same color as the piece you are moving
  sameColor(sqr, piece){
    if(sqr instanceof Piece && sqr.type === piece.type){
      return true;
    }
    return false;
  }
  opposingPieceOnSquare(sqr, piece) {
    if (sqr instanceof Piece && sqr.type !== piece.type) {
      console.log("Shake it on your feet")
      return true;
    }
    return false;
  }
  emptySquare(sqr) {
    console.log(sqr);
    return !(sqr instanceof Piece);
  }
  straightLegalMoves(board, startPos, piece, comp, off) {
    const lMoves = [];
    const [rStart, cStart] = startPos;
    for (let r = rStart + off; comp(r); r += off) {
      if (board[r][cStart] instanceof Piece) {
        if (board[r][cStart].type !== piece.type) {
          lMoves.push([r, cStart]);
        }
        break;
      }
      lMoves.push([r, cStart]);
    }
    return lMoves;
  }
  horizantalLegalMoves(board, startPos, piece, comp, off) {
    const lMoves = [];
    const [rStart, cStart] = startPos;
    for (let c = cStart + off; comp(c); c += off) {
      if (board[rStart][c] instanceof Piece) {
        if (board[rStart][c].type !== piece.type) {
          lMoves.push([rStart, c]);
        }
        break;
      }
      lMoves.push([rStart, c]);
    }
    return lMoves;
  }
}

