const BLACK = false;
const WHITE = true;

class ChessGame {
  constructor(){
    this.turn = WHITE;
    this.selectedPiece = false;
  }

  move(board, startPos, endPos, piece) {
    // If legal move
    const [a, b] = startPos;
    const [x, y] = endPos;
    const legalMoves = piece.legalMoves(board, startPos, piece);
    if(Array.isArray(legalMoves)){
      console.log("It is an array")
    } else {
      console.log("poop")
    }
    console.log(`EndPos = ${endPos}`);
    console.log(`legalMoves = ${legalMoves}`);
    if(!(legalMoves.includes(endPos))){
      return false;
    }

    board[a][b] = "-";
    board[x][y] = piece;

    return board;
  }
}

export default ChessGame;
