import './Square.css';
import React from 'react';

class Square extends React.Component {
  onClickHandler() {
    console.log(this.props.piece)
    this.props.onClick()
  }
  render(){
    const classes = "square" + (this.props.isDark ? " dark" : " light");
    const {piece} = this.props;
    return (
      <td onClick={() => console.log(this.props.piece)} className={classes}>
        {piece !== '-'? 
          <img src={`./images/${piece}.png`}></img> //https://commons.wikimedia.org/wiki/Category:SVG_chess_pieces
        : ''}
      </td>
    );
  }
}

export default Square;
