const BLACK = false;
const WHITE = true;

// Abstract Class
class Piece {
  constructor(type, img, pos){
    if(this.constructor === Piece){
      throw new Error('Class "Piece" cannot be instantiated');
    }
    this.type = type;
    this.imgName = img;
    this.pos = pos;
  }
  // True if row and column are on the board (8x8)
  onBoard(r, c){
    if(r > 7 || r < 0 || c > 7 || c < 0){
      return false;
    }
    return true;
  }
  // True if piece on the square is the same color as the piece you are moving
  sameColor(sqr, piece){
    if(sqr instanceof Piece && sqr.type === piece.type){
      return true;
    }
    return false;
  }
  opposingPieceOnSquare(sqr, piece) {
    if (sqr instanceof Piece && sqr.type !== piece.type) {
      console.log("Shake it on your feet")
      return true;
    }
    return false;
  }
}

class Pawn extends Piece {
  constructor(type, img){
    super(type, img);
  }
  legalMoves(board, pawnPos, pawn) {
    let lMoves = [];
    const [rStart, cStart] = pawnPos;
    console.log(pawn);
    if (pawn.type === WHITE) {
      lMoves.push([rStart + 1, cStart]);
      if(rStart === 1) {
        lMoves.push([rStart + 2, cStart]);
      }
      const [rTake, cTake] = [rStart + 1, cStart + 1];
      console.log(`rTake = ${rTake} cTake = ${cTake}` );
      if (this.opposingPieceOnSquare(board[rTake, cTake], pawn)) {
        lMoves.push([rTake, cTake]);
      }
    } 
    else {
      lMoves.push([rStart - 1, cStart]);
      if(rStart === 6) {
        lMoves.push([rStart - 2, cStart]);
      }
      const takePos = [rStart-1, cStart-1];
    }
    return lMoves;
  }
}

class Bishop extends Piece {
  constructor(type, img){
    super(type, img);
  }
}

class Knight extends Piece {
  constructor(type, img){
    super(type, img);
  }
  legalMoves(board, knightPos, knight) {
    let lMoves = [];
    const [rStart, cStart] = knightPos;
    const moves = [[1,2], [-1,2], [1,-2], [-1,-2], [2,1], [-2,1], [2,-1], [-2,-1]];
    for(let move of moves){
      const [rOff, cOff] = move;
      let r = rStart + rOff;
      let c = cStart + cOff;
      // If move sqr is on the board and does not have one of your pieces on it move is valid
      if(this.onBoard(r, c) && !this.sameColor(board[r][c], knight)){
        const lMove = [r, c];
        lMoves.push(lMove);
      }  
    }
    return lMoves;  
  }
}

class Rook extends Piece {
  constructor(type, img){
    super(type, img);
  }
}

class Queen extends Piece {
  constructor(type, img){
    super(type, img);
  }
}

class King extends Piece {
  constructor(type, img){
    super(type, img);
  }
}


export {Piece, Pawn, Bishop, Knight, Rook, Queen, King};
