const BLACK = 0;
const WHITE = 1;

class Game {
  constructor(){
    this.turn = WHITE;
    this.selectedPiece = false;
  }
  arrayEquals(a, b) {
    return Array.isArray(a) &&
        Array.isArray(b) &&
        a.length === b.length &&
        a.every((val, index) => val === b[index]);
  }

  selectPiece(pos) {
    if(this.arrayEquals(pos, this.state.selectedPiece)){
      console.log(true)
      this.setState({selectedPiece: [-1, -1]})
    }
    this.selectedPiece = pos;
    return this.selectedPiece;
  }



}

export default Game;
