import './Board.css'
import React from 'react'
import Cell from './Square';

class Board extends React.Component {
  static defaultProps = {
    nRows: 8,
    nCols: 8,
  }

  constructor(props){
    super(props);
  }

  state
  render(){
    let board = [];
    let cOdd = true;
    let rOdd = true;
    for(let i = this.props.nRows; i > 0; i--){
      let row = [] 
      cOdd = rOdd
      for(let j = 0; j < this.props.nCols; j++){
        const ltr = `${String.fromCharCode(97 + j)}${i}`
        cOdd = !cOdd;
        const clr = cOdd? 'dark': 'light'
        row.push(<td key={ltr} className={`square ${clr} ${ltr}`}></td>)
        row.push(<Square></Square>)
      }
      board.push(<tr key={i}>{row}</tr>)   
      rOdd = !rOdd;
    }
    return (
      <div>
        <table>
          <tbody>
            {board}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Board;
